const mongoose = require('mongoose')

//_id is auto generated by mongoose
const educationSchema = new mongoose.Schema({
    resumeInfo: {
        type: mongoose.Schema.Types.ObjectId,
        required: true,
        ref: 'resumeInfo'
    },
    institution: {
        type: String,
        required: true
    },
    acheived: { // level of education BA, MA, DR, HS
        type: String,
        required: true,
        maxlength: 2
    },
    program: { //Program of study: i.e. Computer Science
        type: String,
        required: true,
    },
    fromMonth: {
        type: String,
        required: true,
    },
    fromYear: {
        type: Number,
        required: true,
    },
    toMonth: {
        type: String,
        required: true,
    },
    toYear: {
        type: Number,
        required: true,
    },
    city: {
        type: String,
    },
    state: {
        type: String,
        maxlength: 2
    }

    //collection tag makes collection in DB names 'user'
    //timestamps tag adds createdAt and updatedAt fields to schema
}, { collection: 'education', timestamps: true})



module.exports = mongoose.model('Education', educationSchema)